mapper.select:
SELECT c.id AS "categoryId", c.name AS "categoryName", p.id AS "id", p.title AS "title", p.reg_date AS "regDate", p.content AS "content" FROM post p LEFT JOIN jblog.category c on p.category_id = c.id WHERE c.blog_id = #{blogId}
AND category_id = #{categoryId}
ORDER BY reg_date DESC LIMIT 0, 1
AND p.id = #{postId}
;

SELECT post.id                           AS "id",
       title,
       DATE_FORMAT(reg_date, '%Y/%m/%d') AS "regDate"
FROM post
         LEFT JOIN jblog.category c ON post.category_id = c.id
WHERE category_id = #{categoryId}
  AND blog_id = #{blogId}
ORDER BY reg_date DESC, id DESC;
